// è·å– raw_markdown å†…å®¹
const rawMarkdown = $input.first().json.data;

// è½¬æ¢ä¸ºäºŒè¿›åˆ¶æ•°æ®
const binaryData = Buffer.from(rawMarkdown, 'utf8');

const item = $input.first().json;

// å®‰å…¨çš„æ–‡ä»¶åæ¸…ç†å‡½æ•°
function createSafeFilename(title) {
  if (!title) return 'untitled';

  return title
    .replace(/[\/\\:*?"<>|]/g, '-')    // æ›¿æ¢éæ³•å­—ç¬¦ä¸ºæ¨ªçº¿
    .replace(/\s+/g, '_')              // ç©ºæ ¼æ›¿æ¢ä¸ºä¸‹åˆ’çº¿
    .replace(/-{2,}/g, '-')            // å¤šä¸ªæ¨ªçº¿åˆå¹¶
    .replace(/_{2,}/g, '_')            // å¤šä¸ªä¸‹åˆ’çº¿åˆå¹¶
    .replace(/^[-_]+|[-_]+$/g, '')     // ç§»é™¤é¦–å°¾çš„æ¨ªçº¿ä¸‹åˆ’çº¿
    .substring(0, 80);                 // é™åˆ¶é•¿åº¦
}

// ä» target_url æå–ä¸“æ æ ‡è¯†ç¬¦
function extractColumnId(targetUrl) {
  // å¤„ç†ä¸¤ç§æƒ…å†µ: /p/30AI å’Œ /p/30AI/
  const match = targetUrl.match(/\/p\/([^\/]+)\/?$/);
  return match ? match[1] : 'unknown';
}

// è·å–ä¸“æ æ ‡è¯†ç¬¦ (ä»å‰é¢çš„èŠ‚ç‚¹è·å– target_url)
const targetUrl = $('é…ç½®ä¸“æ å‚æ•°').first().json.target_url;
const outputBasePath = $('é…ç½®ä¸“æ å‚æ•°').first().json.output_base_path;
const columnId = extractColumnId(targetUrl);

const safeTitle = createSafeFilename(item.title);
const publishDate = $input.first().json.published_at ? $input.first().json.published_at.split('T')[0] : 'unknown';

// è¿”å›äºŒè¿›åˆ¶æ•°æ®
return [{
  json: {
    columnId: columnId,
    fullPath: `${outputBasePath}/${columnId}/${publishDate}_${safeTitle}.md`
  },
  binary: {
    data: {
      data: binaryData.toString('base64'),
      mimeType: 'text/markdown',
      fileName: `${publishDate}_${safeTitle}`
    }
  }
}];
